example.programlisting:
...
DomainObject obj;

// async i/o call
dispatch_async(dispatch_get_global_queue(0, 0), ^{
// closure as task
// - sync i/o operation on background task
// - delegateSyncIO is the model's sync i/o (controller) delegate
NSString *sth = [[model delegateSyncIO] getSomething];
// - remain in caller's context
// - obj should retain sth
[obj setSomething: sth];

// - dispatch closure to main thread, result dispatch event in main event
// - loop and then finally run the closure on main thread
dispatch_async(dispatch_get_main_queue(), ^{
// closure as task and acturally the callback of the async i/o call
// - another async i/o call
dispatch_async(dispatch_get_global_queue(0, 0), ^{
// - sync i/o operation on background task
NSString *another = [[model delegateSyncIO] getAnother];
// - obj should retain another
[obj setSomething: another];

dispatch_async(dispatch_get_main_queue(), ^{
// - delegateView is the model's view (controller) delegate
[[model delegateView] domainObjectProcessed:obj];});});});});
